mapper.insert:

INSERT INTO USER
(
	USER_ID, 
	PASSWORD, 
	USER_NAME
) VALUES (
	#{userId},
	#{password}, 
	#{userName}
)


INSERT INTO USER_AUTHORITY
(
	USER_ID, 
	AUTHORITY
) VALUES (
	#{userId},
	#{authority}
)

mapper.select:

UPDATE
	USER_AUTHORITY
SET
	AUTHORITY = #{authority}
WHERE
	USER_ID = #{userId}
