mapper.select:
SELECT AAA.* FROM ( SELECT ROW_NUMBER() OVER ( order by ${SIDX} ASC DESC ) RNUM, E.CUSTOMER_IDX, E.CUSTOMER_NAME, E.phone, E.address, E.joindate, E.grade, E.city FROM CUSTOMER AS E
AND ${searchField} like concat('%', #{searchString}, '%')
) AAA WHERE AAA.RNUM BETWEEN ${startCount} AND ${endCount}

mapper.insert:

INSERT INTO CUSTOMER
(
	customer_name, 
	address,
	grade
) VALUES (
	#{customerName}, 
	#{address},
	#{grade}
)
